import React,{useState,useEffect} from "react"

function App(){
	const[time,setTime]=useState(7)
	const[text,setText]=useState("")
	const[isTimeRunning,isTimeRemaining]=useState(false)
	const[wordCount,setWordCount]=useState(0)
	function handleChange(e){
		const[value]=e.target
		  setText(value)
	}
	function calculateTextCount(text){
	  const wordArr= text.trim().split(" ");
	  return wordArr.filter(word=>word!=" ").length
	}
	function startGame(){
		isTimeRunning(true)
	}
	
	function endGame(){
		setWordCount(calculateTextCount(text))
	}
	useEffect(()=>{
		if(time>0){
		setTimeout(()=>{
			setTime(time1=>time1-1)
		},1000)
	}
	},[time,isTimeRemaining])
	return(
		<div>
			<h1>how fast you can TYPE</h1>
			<textarea value={text}
			onChange={handleChange}
			></textarea>
			<h3>Time remaining: {time}</h3>
			<button
             onClick={startGame}
			>start</button>
			<h2>word count: {wordCount}</h2>
		</div>
	)
}
export default App
